function createConfiguration(n,t){return{$type:"Spin.Modules.Entity.ModuleConfiguration, Spin.Modules",Id:n,Value:t}}$("#btnSaveConfiguration").click(function(){$("#loadingConfiguration").show();var n=[];$.each($("#frmConfiguration input"),function(t,i){var r=$(this).attr("configuration-id"),i=$(this).val();n.push(createConfiguration(r,i))});$.ajax({method:"POST",url:"/Api/Util/ConfigurationApi/Save",data:JSON.stringify(n),contentType:"application/json",dataType:"json"}).done(function(n){$.each(n,function(n,t){$("#id"+t.name).attr("configuration-id",t.id)});$("#loadingConfiguration").hide()}).fail(function(){$("#loadingConfiguration").hide()})});